// App.js - Simple Video Analysis App
import React, { useState, useRef, useEffect } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  // Simple state management - each variable has one clear purpose
  const [videoUrl, setVideoUrl] = useState('');           // YouTube URL input
  const [question, setQuestion] = useState('');           // User's question
  const [messages, setMessages] = useState([]);           // Chat messages
  const [loading, setLoading] = useState(false);          // Loading state
  const [videoId, setVideoId] = useState('');             // Current video ID
  const [videoTitle, setVideoTitle] = useState('');       // Video title
  const [timestamps, setTimestamps] = useState([]);       // Auto-generated timestamps
  const [error, setError] = useState('');                 // Error messages
  const [notifications, setNotifications] = useState([]); // Right-side notifications

  const chatRef = useRef(null);

  // Auto-scroll chat when new messages arrive
  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages]);

  // Show notification (right side alert)
  const showNotification = (message, type = 'info', persistent = false) => {
    const id = Date.now();
    const newNotification = { id, message, type, persistent };
    setNotifications(prev => [...prev, newNotification]);

    // Auto-remove after 4 seconds (unless it's persistent)
    if (!persistent) {
      setTimeout(() => {
        setNotifications(prev => prev.filter(n => n.id !== id));
      }, 4000);
    }

    return id; // Return ID so we can remove it manually later
  };

  // Remove specific notification by ID
  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  // Get YouTube video ID from URL
  const getVideoId = (url) => {
    const patterns = [
      /youtube\.com\/watch\?v=([^&\n?#]+)/,
      /youtu\.be\/([^&\n?#]+)/,
      /youtube\.com\/embed\/([^&\n?#]+)/
    ];

    for (let pattern of patterns) {
      const match = url.match(pattern);
      if (match) return match[1];
    }
    return null;
  };

  // Load video and auto-generate timestamps
// üéØ FIXED: Use real timestamp data from backend instead of manual generation
const loadVideo = async () => {
  console.log('üîç DEBUG: loadVideo called with new code!'); // Add this line first
  const id = getVideoId(videoUrl);
  if (!id) {
    setError('Please enter a valid YouTube URL');
    return;
  }

  setVideoId(id);
  setMessages([]);
  setTimestamps([]);
  setError('');

  // Show persistent loading notification for video processing
  const videoLoadingId = showNotification('üé¨ Loading video and extracting transcript', 'loading', true);

  try {
    // First, try to get the transcript to ensure video is processable
    const transcriptResponse = await fetch(`${API_BASE_URL}/process-youtube`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ url: videoUrl })
    });

  const transcriptData = await transcriptResponse.json();

  // üîç ADD THIS DEBUG LINE:
  console.log('üîç DEBUG - API Response:', {
    success: transcriptData.success,
    has_timestamps: transcriptData.has_timestamps,
    structured_transcript: transcriptData.structured_transcript ? 'Present' : 'Missing',
    method_used: transcriptData.method_used,
    total_segments: transcriptData.total_segments
  });

  if (!transcriptData.success) {
      removeNotification(videoLoadingId);
      showNotification('‚ùå Failed to process video: ' + transcriptData.error, 'error');
      setError('Could not extract transcript from this video');
      return;
    }

    // Video processed successfully - remove loading notification
    removeNotification(videoLoadingId);
    showNotification('‚úÖ Video transcript extracted successfully!', 'success');

    // Set video title from transcript response
    setVideoTitle(transcriptData.video_title || 'Unknown Video');

    // Add welcome message
    setMessages([{
      type: 'system',
      content: `Video loaded! Ask questions about "${transcriptData.video_title || 'this video'}".`,
      time: new Date().toLocaleTimeString()
    }]);

    // üéØ NEW: Use real timestamp data from backend instead of generating manually
    if (transcriptData.has_timestamps && transcriptData.structured_transcript) {
      generateRealTimestamps(transcriptData.structured_transcript);
    } else {
      // Fallback: Generate chapters using AI if no structured data
      await generateTimestamps(videoUrl);
    }

  } catch (error) {
    removeNotification(videoLoadingId);
    showNotification('‚ùå Connection error: ' + error.message, 'error');
    setError('Failed to connect to server');
  }
};

// üéØ NEW: Use real structured timestamp data to create meaningful chapters
const generateRealTimestamps = (structuredTranscript) => {
  const timestampLoadingId = showNotification('üïê Creating chapters from real timestamps...', 'loading', true);

  try {
    const chapters = [];
    const totalSegments = structuredTranscript.length;

    // Create chapters every ~2-3 minutes (adaptive based on video length)
    const segmentsPerChapter = Math.max(50, Math.floor(totalSegments / 15)); // Aim for ~15 chapters max

    for (let i = 0; i < totalSegments; i += segmentsPerChapter) {
      const segment = structuredTranscript[i];

      if (segment && segment.text && segment.start_formatted) {
        // Create meaningful chapter title from content
        let title = segment.text.trim();

        // Clean up common phrases
        title = title.replace(/^(welcome to|hello|hi there|today we|in this)/i, '');
        title = title.replace(/^(fact|number|tip)( \d+)?:?/i, '');

        // Take first few meaningful words
        const words = title.split(' ').filter(word => 
          word.length > 2 && 
          !['the', 'and', 'but', 'for', 'are', 'will', 'can', 'you', 'your'].includes(word.toLowerCase())
        );

        const chapterTitle = words.slice(0, 3).join(' ');

        if (chapterTitle.length > 3) {
          const [minutes, seconds] = segment.start_formatted.split(':').map(Number);

          chapters.push({
            time: segment.start_formatted,
            title: chapterTitle.length > 30 ? chapterTitle.substring(0, 27) + '...' : chapterTitle,
            seconds: minutes * 60 + seconds
          });
        }
      }
    }

    // Add final chapter
    if (structuredTranscript.length > 0) {
      const lastSegment = structuredTranscript[structuredTranscript.length - 1];
      if (lastSegment && lastSegment.end_formatted) {
        const [minutes, seconds] = lastSegment.end_formatted.split(':').map(Number);
        chapters.push({
          time: lastSegment.end_formatted,
          title: 'Conclusion',
          seconds: minutes * 60 + seconds
        });
      }
    }

    setTimestamps(chapters);
    removeNotification(timestampLoadingId);
    showNotification('‚úÖ Real timestamps generated!', 'success');

  } catch (error) {
    console.error('Error generating real timestamps:', error);
    removeNotification(timestampLoadingId);
    showNotification('‚ö†Ô∏è Using fallback timestamp generation', 'error');
    // Fallback to AI generation
    generateTimestamps(videoUrl);
  }
};

// üéØ KEEP: Fallback function for when structured data isn't available
const generateTimestamps = async (url) => {
  const timestampLoadingId = showNotification('üïê Generating timestamps...', 'loading', true);

  try {
    const response = await fetch(`${API_BASE_URL}/smart-question`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        url: url,
        question: "Create video chapters with short titles. Format: '0:00 - Introduction', '2:30 - Main Topic', etc. Keep titles under 5 words."
      })
    });

    const data = await response.json();

    if (data.success) {
      // Update video title if we got it from this response
      if (data.video_title && !videoTitle) {
        setVideoTitle(data.video_title);
      }

      // Extract timestamps from AI response
      const lines = data.answer.split('\n');
      const newTimestamps = [];

      lines.forEach(line => {
        const match = line.match(/(\d{1,2}):(\d{2})\s*[-‚Äì]\s*(.+)/);
        if (match) {
          const minutes = parseInt(match[1]);
          const seconds = parseInt(match[2]);
          const title = match[3].trim().replace(/\*\*/g, ''); // Remove markdown

          newTimestamps.push({
            time: `${minutes}:${seconds.toString().padStart(2, '0')}`,
            title: title.length > 40 ? title.substring(0, 37) + '...' : title,
            seconds: minutes * 60 + seconds
          });
        }
      });

      setTimestamps(newTimestamps);
      removeNotification(timestampLoadingId);
      showNotification('‚úÖ AI timestamps generated!', 'success');
    } else {
      removeNotification(timestampLoadingId);
      showNotification('‚ö†Ô∏è Could not generate timestamps', 'error');
    }
  } catch (error) {
    removeNotification(timestampLoadingId);
    showNotification('‚ùå Failed to generate timestamps', 'error');
  }
};
  // Ask question about video
  const askQuestion = async () => {
    if (!question.trim()) {
      setError('Please enter a question');
      return;
    }
    if (!videoUrl.trim()) {
      setError('Please load a video first');
      return;
    }

    setLoading(true);
    setError('');

    // Add user message
    const userMessage = {
      type: 'user',
      content: question
    };
    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await fetch(`${API_BASE_URL}/smart-question`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          url: videoUrl,
          question: question + " Please include specific timestamps in your response using format (2:30) without extra words like 'around' or 'at'."
        })
      });

      const data = await response.json();

      if (data.success) {
        const aiMessage = {
          type: 'ai',
          content: data.answer,
          method: data.method_used
        };
        setMessages(prev => [...prev, aiMessage]);
      } else {
        setError(`Failed: ${data.error}`);
      }
    } catch (error) {
      setError(`Connection error: ${error.message}`);
    } finally {
      setLoading(false);
      setQuestion('');
    }
  };

  // Handle Enter key
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !loading) {
      askQuestion();
    }
  };

  // Jump to timestamp in video
  const jumpToTime = (seconds) => {
    const iframe = document.getElementById('youtube-player');
    if (iframe) {
      const baseUrl = iframe.src.split('?')[0];
      iframe.src = `${baseUrl}?start=${seconds}&autoplay=1`;
    }
  };

  // Simple text formatter - handles markdown and timestamps
  const formatText = (text) => {
    if (typeof text !== 'string') return text;

    // Clean up markdown and convert to HTML
    let formattedText = text
      .replace(/\*\*([^*]+)\*\*/g, '<strong class="bold-text">$1</strong>')  // **bold**
      .replace(/\*([^*]+)\*/g, '<em class="italic-text">$1</em>')            // *italic*
      .replace(/\((\d{1,2}):(\d{2})\)/g, '<span class="timestamp-placeholder">$1:$2</span>'); // timestamps

    // Split by timestamp placeholders and create clickable buttons
    const parts = formattedText.split(/<span class="timestamp-placeholder">(\d{1,2}:\d{2})<\/span>/);
    const result = [];

    for (let i = 0; i < parts.length; i++) {
      if (i % 2 === 0) {
        // Regular text with HTML formatting
        if (parts[i]) {
          result.push(
            <span 
              key={i} 
              dangerouslySetInnerHTML={{ __html: parts[i] }}
            />
          );
        }
      } else {
        // Timestamp - make it clickable
        const [minutes, seconds] = parts[i].split(':').map(Number);
        const totalSeconds = minutes * 60 + seconds;
        result.push(
          <button
            key={i}
            className="timestamp-link"
            onClick={() => jumpToTime(totalSeconds)}
          >
            {parts[i]}
          </button>
        );
      }
    }

    return result.length > 0 ? result : text;
  };

  return (
    <div className="App">
      {/* Notifications - Right Side */}
      <div className="notifications">
        {notifications.map(notification => (
          <div key={notification.id} className={`notification ${notification.type}`}>
            {notification.message}
          </div>
        ))}
      </div>

      {/* Header */}
      <header className="header">
        <h1>üé¨ Video Analysis AI</h1>
        <p>Ask questions about any YouTube video!</p>
      </header>

      {/* Main Content */}
      <div className="main-content">

        {/* Video Section */}
        <div className="video-section">
          {/* URL Input */}
          <div className="url-input">
            <h3>üìπ Load YouTube Video</h3>
            <div className="input-group">
              <input
                type="text"
                placeholder="Paste YouTube URL here..."
                value={videoUrl}
                onChange={(e) => setVideoUrl(e.target.value)}
                className="url-field"
              />
              <button onClick={loadVideo} className="load-btn">
                Load Video
              </button>
            </div>
          </div>

          {/* YouTube Player */}
          {videoId && (
            <div className="video-player">
              <iframe
                id="youtube-player"
                src={`https://www.youtube.com/embed/${videoId}`}
                title="YouTube video player"
                frameBorder="0"
                allowFullScreen
              ></iframe>
            </div>
          )}

          {/* Auto-Generated Timestamps */}
          {timestamps.length > 0 && (
            <div className="timestamps-section">
              <h4>üïê Video Chapters</h4>
              <p>Click any timestamp to jump to that moment</p>
              <div className="timestamps-list">
                {timestamps.map((item, index) => (
                  <div
                    key={index}
                    className="timestamp-item"
                    onClick={() => jumpToTime(item.seconds)}
                  >
                    <span className="time">{item.time}</span>
                    <span className="title">{item.title}</span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Chat Section */}
        <div className="chat-section">
          <div className="chat-header">
            <h3>üí¨ Ask Questions</h3>
            {videoTitle && <div className="video-title">üé¨ {videoTitle}</div>}
          </div>

          {/* Chat Messages */}
          <div className="chat-messages" ref={chatRef}>
            {messages.length === 0 && (
              <div className="welcome">
                <h4>Welcome! üöÄ</h4>
                <p>Load a video above and start asking questions</p>
                <div className="examples">
                  <div className="example">üí¨ "What is this video about?"</div>
                  <div className="example">üé® "What color is the car?"</div>
                  <div className="example">üìù "Summarize the main points"</div>
                </div>
              </div>
            )}

            {messages.map((message, index) => (
              <div key={index} className={`message ${message.type}`}>

                {/* System Message */}
                {message.type === 'system' && (
                  <div className="system-msg">
                    <span>{message.content}</span>
                  </div>
                )}

                {/* User Message */}
                {message.type === 'user' && (
                  <div className="user-msg">
                    <div className="avatar">You</div>
                    <div className="content">
                      <div className="text">{message.content}</div>
                    </div>
                  </div>
                )}

                {/* AI Message */}
                {message.type === 'ai' && (
                  <div className="ai-msg">
                    <div className="avatar">ü§ñ</div>
                    <div className="content">
                      <div className="badge">
                        {message.method === 'Visual Analysis' ? 'üëÅÔ∏è Visual' : 'üìù Text'}
                      </div>
                      <div className="text">
                        {formatText(message.content)}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ))}

            {/* Loading Message */}
            {loading && (
              <div className="message ai">
                <div className="ai-msg">
                  <div className="avatar">ü§ñ</div>
                  <div className="content">
                    <div className="loading">Analyzing video</div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Error Display */}
          {error && (
            <div className="error">
              ‚ö†Ô∏è {error}
              <button onClick={() => setError('')}>√ó</button>
            </div>
          )}

          {/* Question Input */}
          <div className="question-input">
            <div className="input-group">
              <input
                type="text"
                placeholder="Ask any question about the video..."
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                onKeyPress={handleKeyPress}
                disabled={loading}
                className="question-field"
              />
              <button 
                onClick={askQuestion}
                disabled={loading || !question.trim() || !videoId}
                className="ask-btn"
              >
                {loading ? '‚è≥' : 'üöÄ'}
              </button>
            </div>

            {videoId && (
              <div className="hint">
                üí° Ask about content, visuals, or request summaries!
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>‚ö° Powered by AI ‚Ä¢ üéØ Smart Analysis ‚Ä¢ ‚è∞ Auto-Timestamps</p>
      </footer>
    </div>
  );
}

export default App;
